import * as React from '@material-ui/core/Backdrop/node_modules/react';
import { StandardProps } from '@material-ui/core';
import { FadeProps } from '@material-ui/core/Fade';
import { TransitionProps } from '@material-ui/core/transitions/transition';

export interface BackdropProps
  extends StandardProps<
    React.HTMLAttributes<HTMLDivElement> & Partial<FadeProps>,
    BackdropClassKey
  > {
  invisible?: boolean;
  onClick?: React.ReactEventHandler<{}>;
  open: boolean;
  transitionDuration?: TransitionProps['timeout'];
}

export type BackdropClassKey = 'root' | 'invisible';

declare const Backdrop: React.ComponentType<BackdropProps>;

export default Backdrop;
